		java.toast(t);
		java.log(t);
	}
getv=()=>{
	try{
		$=JSON.parse(source.getVariable());
	}catch(err){$=null}
	return $
}
setv=(json)=>source.setVariable(JSON.stringify(json));
yn=yn=>yn?"启用":"禁用";
