	["leeyegy", "souxs.leeyegy","scxs.pysmei"],
	["jiaston", "sou.jiaston","content.shenbabao"]
];


look=()=>{
	t = "【查看当前设置】"+
	"\n\t<搜索设置>\n\t\t\t\t当前接口："+u[$.a.c][0]+

	"\n\t<发现设置>\n\t\t\t\t显示最新章节："+yn($.b)+

	"\n\t<目录设置>\n\t\t\t\t目录分卷："+yn($.c.a)+
	"\n\t\t\t\t章节名显示分卷名："+yn($.c.b)+
	"\n\t\t\t\t章节名净化："+yn($.c.c)+
	"\n\t\t\t\t列表屏蔽："+yn($.c.d)
	return login(t)
}





//启用/禁用
$$=(type,name)=>{
		i = eval('$.'+type);
login('【'+name+'】设置\n\t\t'+yn(i)+'=>'+yn(!i));
		eval('$.'+type+'='+!i);
		return setv($);
	}

//接口选择
$$$=(index)=>{
	if(index!=$.a.c){
		$.a.d = index
login('确定选择【'+u[$.a.d][0]+'】为当前搜索接口吗？')
		return setv($);
	}
}
//确定启用
function yes(){
if( $.a.d!=$.a.c ){
		$.a.c = $.a.d
		$.a.b = "https://"+u[$.a.c][2]+".com"
		$.a.a = "https://"+u[$.a.c][1]+".com"
		login('【启用成功提示】\n\n\n　　启用成功，已选择 【'+u[$.a.c][0]+'】 为当前搜索接口')
		return setv($);
	}
}


